import React, { useState } from 'react';
import { Button, ButtonGroup, Typography, Paper, Container, CssBaseline, Box, Link } from '@mui/material';
import { styled, makeStyles } from '@mui/styles';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import TableCell from '@mui/material/TableCell';
import TableRow from '@mui/material/TableRow';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import SupervisorAccountIcon from '@mui/icons-material/SupervisorAccount';
import ResearchIcon from '@mui/icons-material/Science';
import BuildIcon from '@mui/icons-material/Build';
import SaveIcon from '@mui/icons-material/Save';
import useTheme from '@mui/material/styles/useTheme';



const useStyles = makeStyles((theme) => ({
  navbarContainer: {
    backgroundColor: '#fcfcfc',
    color: '#333',
    borderBottom: '1px solid #ddd',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  navbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '0.5rem',
  },
  logo: {
    fontSize: '1.6rem',
    fontWeight: 'bold',
    textDecoration: 'none',
    color: '#333',
  },
  logoText: {
    fontFamily: 'Helvetica, Arial, sans-serif',
  },
  navLinks: {
    display: 'flex',
    gap: '1.5rem',
  },
  navLink: {
    fontSize: '1.2rem',
    color: '#333',
    textDecoration: 'none',
    padding: '0.5rem',
    borderRadius: '0.25rem',
    transition: 'background-color 0.3s ease',
    fontFamily: 'Roboto, sans-serif',
  },
  navLinkHover: {
    '&:hover': {
      backgroundColor: '#eee',
    },
  },
  tableHeader: {
    backgroundColor: '#f5f5f5',
    fontWeight: 'bold',
    fontFamily: 'Helvetica, Arial, sans-serif',
  },
}));

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  color: theme.palette.common.black,
  border: '1px solid #ddd', // División en cada columna
  padding: '0.5rem',
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: '#ffffff',
  },
  '&:nth-of-type(even)': {
    backgroundColor: '#f5f5f5',
  },
}));

const StyledButton = styled(Button)(({ theme }) => ({
  margin: '0 5px',
}));

const users = [
  { id: 1, name: 'Juan Araque', email: 'jaraque129@puce.edu.ec' },
  { id: 2, name: 'Usuario Puce', email: 'usuario@puce.edu.ec' },
  { id: 3, name: 'Usuario Puce', email: 'usuario@puce.edu.ec' },
  { id: 4, name: 'Usuario Puce', email: 'usuario@puce.edu.ec' },
  { id: 5, name: 'Usuario Puce', email: 'usuario@puce.edu.ec' },
];

function RoleAssignment() {
  const classes = useStyles();
  const theme = useTheme();
  const [buttonColors, setButtonColors] = useState(Array(users.length * 3).fill("primary"));

  const handleClick = (index) => {
    const newButtonColors = [...buttonColors];
    newButtonColors[index] = newButtonColors[index] === "primary" ? "secondary" : "primary";
    setButtonColors(newButtonColors);
  };

  return (
    <div className={classes.navbarContainer}>
      <AppBar position="static" color="transparent" elevation={0}>
        <Container maxWidth="lg">
          <Toolbar className={classes.navbar}>
            <Typography variant="h6" component={Link} to="/" className={classes.logo}>
              <span className={classes.logoText}>Bio-Inka</span>
            </Typography>
            <Box className={classes.navLinks}>
              <Link to="/inicio/geo" className={`${classes.navLink} ${classes.navLinkHover}`}>Georreferenciación</Link>
              <Link to="/inicio/iniciova" className={`${classes.navLink} ${classes.navLinkHover}`}>Destacados</Link>
              <Link to="/inicio/about" className={`${classes.navLink} ${classes.navLinkHover}`}>Acerca de nosotros</Link>
              <Link to="/inicio/login" className={`${classes.navLink} ${classes.navLinkHover}`}>Log-In</Link>
            </Box>
          </Toolbar>
        </Container>
      </AppBar>

      <Container component="main" maxWidth="md" sx={{ py: 5, my: 8, boxShadow: 3 }}>
        <CssBaseline />
        <Typography variant="h5" align="center" gutterBottom>Asignación de Roles</Typography>
        <TableContainer component={Paper} sx={{backgroundColor: 'transparent'}}>
          <Table sx={{ minWidth: 700 }} aria-label="customized table">
            <TableHead>
              <TableRow>
                <StyledTableCell className={classes.tableHeader}>Usuarios</StyledTableCell>
                <StyledTableCell className={classes.tableHeader}>Email</StyledTableCell>
                <StyledTableCell className={classes.tableHeader}>Asignar Rol</StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {users.map((user, index) => (
                <StyledTableRow key={user.id}>
                  <StyledTableCell component="th" scope="row">
                    {user.name}
                  </StyledTableCell>
                  <StyledTableCell>{user.email}</StyledTableCell>
                  <StyledTableCell>
                    <ButtonGroup variant="text" aria-label="outlined primary button group">
                      <StyledButton startIcon={<SupervisorAccountIcon />} variant={buttonColors[index*3] === "primary" ? "outlined" : "contained"} color="primary" onClick={() => handleClick(index*3)}>Administrador</StyledButton>
                      <StyledButton startIcon={<ResearchIcon />} variant={buttonColors[index*3+1] === "primary" ? "outlined" : "contained"} color="primary" onClick={() => handleClick(index*3+1)}>Investigador</StyledButton>
                      <StyledButton startIcon={<BuildIcon />} variant={buttonColors[index*3+2] === "primary" ? "outlined" : "contained"} color="primary" onClick={() => handleClick(index*3+2)}>Técnico</StyledButton>
                    </ButtonGroup>
                  </StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
          <Button variant="contained" color="primary" startIcon={<SaveIcon />} sx={{ mt: 2, mb: 1, float: 'right' }}>Guardar</Button>
        </TableContainer>
      </Container>
    </div>
  );
}

export default RoleAssignment;
