import React, { useState, useEffect } from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem from "@mui/lab/TreeItem";
import { Box, Grid } from "@mui/material";
import axios from 'axios';

const StyledTreeItem = styled(TreeItem)(({ theme }) => ({
  marginLeft: theme.spacing(2),
  "& .MuiTreeItem-content": {
    color: "#000000",
  },
}));

export default function TaskForm() {
  const [reinos, setReinos] = useState([]);
  const [filos, setFilos] = useState([]);
  const [selectedReino, setSelectedReino] = useState("");

  useEffect(() => {
    const fetchReinos = async () => {
      try {
        const response = await axios.get("http://localhost:4000/reinos");
        setReinos(response.data);
      } catch (error) {
        console.log("Error fetching reinos data: ", error);
      }
    };

    fetchReinos();
  }, []);

  const fetchFilos = async (reinoId) => {
    try {
      const response = await axios.get(`http://localhost:4000/filos/${reinoId}`);
      setFilos(response.data);
    } catch (error) {
      console.log("Error fetching filos data: ", error);
    }
  };

  const handleReinoClick = (reinoId) => {
    setSelectedReino(reinoId);
    fetchFilos(reinoId);
  };

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TreeView
            defaultCollapseIcon={<ExpandMoreIcon />}
            defaultExpandIcon={<ChevronRightIcon />}
            sx={{ height: 240, flexGrow: 1 }}
          >
            {reinos.map((reino) => (
              <StyledTreeItem
                key={reino.rei_id}
                nodeId={reino.rei_id.toString()}
                label={reino.rei_nombre.trim()}
                onClick={() => handleReinoClick(reino.rei_id)}
              >
                {selectedReino === reino.rei_id && filos.length > 0 ? (
                  filos.map((filo) => (
                    <StyledTreeItem
                      key={filo.fil_id}
                      nodeId={filo.fil_id.toString()}
                      label={filo.fil_nombre.trim()}
                    />
                  ))
                ) : (
                  <></>
                )}
              </StyledTreeItem>
            ))}
          </TreeView>
        </Grid>
      </Grid>
    </Box>
  );
}
