import React, { useState } from 'react';
import { TextField, Button, Grid, makeStyles } from '@mui/material';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  formContainer: {
    width: '400px',
    margin: 'auto',
    '& .MuiTextField-root': {
      marginBottom: theme.spacing(2),
    },
  },
}));

const Formulario = () => {
  const classes = useStyles();

  const [clase, setClase] = useState('');
  const [orden, setOrden] = useState('');
  const [familia, setFamilia] = useState('');
  const [genero, setGenero] = useState('');
  const [especie, setEspecie] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Insertar Clase
      await axios.post('http://localhost:3000/clases', { cla_id: '100', fil_id: '21', cla_nombre: 'Gastropoda' });

      // Insertar Orden
      await axios.post('http://localhost:3000/ordenes', { ord_id: '200', cla_id: '100', ord_nombre: 'Stylommatophora' });

      // Insertar Familia
      await axios.post('http://localhost:3000/familias', { fam_id: '300', ord_id: '200', gen_nombre: 'Helicidae' });

      // Insertar Genero
      await axios.post('http://localhost:3000/generos', { gen_id: '400', fam_id: '300', gen_nombre: 'Cornu' });

      // Insertar Especie
      await axios.post('http://localhost:3000/especies', { esp_id: '600', gen_id: '400', esp_nombre: 'C. aspersum', esp_sexo: 'macho' });

      console.log('Inserciones exitosas');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className={classes.formContainer}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Clase"
            value={clase}
            onChange={(e) => setClase(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Orden"
            value={orden}
            onChange={(e) => setOrden(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Familia"
            value={familia}
            onChange={(e) => setFamilia(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="GÃ©nero"
            value={genero}
            onChange={(e) => setGenero(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Especie"
            value={especie}
            onChange={(e) => setEspecie(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <Button type="submit" variant="contained" color="primary">
            Guardar
          </Button>
        </Grid>
      </Grid>
    </form>
  );
};

export default Formulario;
