import React, { useState } from 'react';
import axios from 'axios';

const Formulario = () => {
  const [clase, setClase] = useState('');
  const [orden, setOrden] = useState('');
  const [familia, setFamilia] = useState('');
  const [genero, setGenero] = useState('');
  const [especie, setEspecie] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Insertar Clase
      await axios.post('http://localhost:3000/clases', { cla_id: '100', fil_id: '21', cla_nombre: 'Gastropoda' });

      // Insertar Orden
      await axios.post('http://localhost:3000/ordenes', { ord_id: '200', cla_id: '100', ord_nombre: 'Stylommatophora' });

      // Insertar Familia
      await axios.post('http://localhost:3000/familias', { fam_id: '300', ord_id: '200', gen_nombre: 'Helicidae' });

      // Insertar Genero
      await axios.post('http://localhost:3000/generos', { gen_id: '400', fam_id: '300', gen_nombre: 'Cornu' });

      // Insertar Especie
      await axios.post('http://localhost:3000/especies', { esp_id: '600', gen_id: '400', esp_nombre: 'C. aspersum', esp_sexo: 'macho' });

      console.log('Inserciones exitosas');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Clase:</label>
        <input type="text" value={clase} onChange={(e) => setClase(e.target.value)} />
      </div>
      <div>
        <label>Orden:</label>
        <input type="text" value={orden} onChange={(e) => setOrden(e.target.value)} />
      </div>
      <div>
        <label>Familia:</label>
        <input type="text" value={familia} onChange={(e) => setFamilia(e.target.value)} />
      </div>
      <div>
        <label>GÃ©nero:</label>
        <input type="text" value={genero} onChange={(e) => setGenero(e.target.value)} />
      </div>
      <div>
        <label>Especie:</label>
        <input type="text" value={especie} onChange={(e) => setEspecie(e.target.value)} />
      </div>
      <button type="submit">Guardar</button>
    </form>
  );
};

export default Formulario;
