import React from 'react';
import { Button, ButtonGroup, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography, Paper, Container, CssBaseline } from '@mui/material';
import { styled } from '@mui/system';
import { ThemeProvider, createTheme } from '@mui/material/styles';

// Crear un tema personalizado
const theme = createTheme({
  palette: {
    background: {
      default: '#cfd8dc',  // Color de fondo de la página
    },
  },
});

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  backgroundColor: theme.palette.common.white,
  color: theme.palette.common.black,
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
}));

const users = [
  { id: 1, name: 'User1', email: 'user1@mail.com' },
  { id: 2, name: 'User2', email: 'user2@mail.com' },
  { id: 3, name: 'User3', email: 'user3@mail.com' },
  { id: 4, name: 'User4', email: 'user4@mail.com' },
  { id: 5, name: 'User5', email: 'user5@mail.com' },
];

function RoleAssignment() {
  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="md" sx={{ py: 5, my: 8, boxShadow: 3 }}>
        <CssBaseline />
        <Typography variant="h5" align="center" gutterBottom>Asignación de Roles</Typography>
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 700 }} aria-label="customized table">
            <TableHead>
              <TableRow>
                <StyledTableCell>Usuarios</StyledTableCell>
                <StyledTableCell>Email</StyledTableCell>
                <StyledTableCell>Asignar Rol</StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {users.map((user) => (
                <StyledTableRow key={user.id}>
                  <StyledTableCell component="th" scope="row">
                    {user.name}
                  </StyledTableCell>
                  <StyledTableCell>{user.email}</StyledTableCell>
                  <StyledTableCell>
                    <ButtonGroup variant="contained" color="primary" aria-label="outlined primary button group">
                      <Button>Administrador</Button>
                      <Button>Investigador</Button>
                      <Button>Técnico</Button>
                    </ButtonGroup>
                  </StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
    </ThemeProvider>
  );
}

export default RoleAssignment;
