import React from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem, { treeItemClasses } from "@mui/lab/TreeItem";
import { Grid, Typography } from "@mui/material";

const StyledTreeItem = styled(TreeItem)(({ rootNode }) => {
  const borderColor = "gray";

  return {
    position: "relative",
    "&:before": {
      pointerEvents: "none",
      content: '""',
      position: "absolute",
      width: 32,
      left: -16,
      top: 12,
      borderBottom: !rootNode ? `1px dashed ${borderColor}` : "none"
    },
    [`& .${treeItemClasses.group}`]: {
      marginLeft: 16,
      paddingLeft: 18,
      borderLeft: `1px dashed ${borderColor}`
    }
  };
});

const StyledTreeView = styled(TreeView)({
  "& .MuiTreeItem-content": {
    color: "#000000", // Cambiar el color del texto cuando el árbol se despliega
  },
});

export default function FileSystemNavigator() {
  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        {/* Descripción */}
        <Typography variant="h5" sx={{ color: "#000000", marginBottom: 2 }}>
          Descripción
        </Typography>
        <Typography variant="body1" color="#000000" fontFamily="Helvetica Neue">
          Nuestro sitio web es una plataforma diseñada para facilitar la georreferenciación de datos de especies. Con su interfaz fácil de usar y su funcionalidad avanzada, permite a los investigadores, administradores y usuarios administrar y analizar de manera eficiente la información sobre especies.
        </Typography>
      </Grid>
      <Grid item xs={12}>
        {/* Árbol */}
        <StyledTreeView
          defaultCollapseIcon={<ExpandMoreIcon />}
          defaultExpandIcon={<ChevronRightIcon />}
          sx={{ height: 240, flexGrow: 1 }}
        >
          <StyledTreeItem rootNode nodeId="1" label="Applications">
            <StyledTreeItem nodeId="2" label="Calendar" />
            <StyledTreeItem nodeId="3" label="Drive" />
            <StyledTreeItem nodeId="4" label="Inbox">
              <StyledTreeItem nodeId="5" label="One" />
              <StyledTreeItem nodeId="6" label="Two" />
              <StyledTreeItem nodeId="7" label="Three" />
            </StyledTreeItem>
          </StyledTreeItem>
          <StyledTreeItem rootNode nodeId="8" label="Documents">
            <StyledTreeItem nodeId="9" label="OSS" />
            <StyledTreeItem nodeId="10" label="MUI">
              <StyledTreeItem nodeId="11" label="index.js" />
            </StyledTreeItem>
          </StyledTreeItem>
        </StyledTreeView>
      </Grid>
    </Grid>
  );
}
