import React, { useState, useEffect } from 'react';
import TreeView from '@mui/lab/TreeView';
import TreeItem, { treeItemClasses } from '@mui/lab/TreeItem';
import { styled } from '@mui/material/styles';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { Grid, Typography, Box, TextField, IconButton } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

const theme = createTheme({
  components: {
    MuiTreeItem: {
      styleOverrides: {
        label: {
          color: '#000000',
        },
        iconContainer: {
          color: '#000000',
        },
      },
    },
  },
});

function TaxonomyTree() {
  const [treeData, setTreeData] = useState([]);

  function createTreeNode(id, name, children, level) {
    return { id: id.toString(), name: name.trim(), children, level };
  }

  function getNextLevel(currentLevel) {
    switch (currentLevel) {
      case 'reino':
        return 'filo';
      case 'filo':
        return 'clase';
      case 'clase':
        return 'orden';
      case 'orden':
        return 'familia';
      case 'familia':
        return 'genero';
      case 'genero':
        return 'especie';
      default:
        return '';
    }
  }

  async function loadNodeChildren(node) {
    const level = node.level;
    const nextLevel = getNextLevel(level);
    let url = `http://localhost:4000/${nextLevel}s/${node.id}`;

    try {
      const response = await fetch(url);
      const data = await response.json();
      const childNodes = data.map((item) =>
        createTreeNode(item.id, item.nombre, [], nextLevel)
      );
      return { ...node, children: childNodes };
    } catch (error) {
      console.error('Error fetching data: ', error);
      return node;
    }
  }

  async function fetchData() {
    try {
      const response = await fetch('http://localhost:4000/reinos');
      const data = await response.json();
      const nodes = data.map((item) =>
        createTreeNode(item.rei_id, item.rei_nombre, [], 'reino')
      );
      setTreeData(nodes);
    } catch (error) {
      console.error('Error fetching data: ', error);
    }
  }

  useEffect(() => {
    fetchData();
  }, []);

  const handleNodeToggle = async (event, nodeId) => {
    const nodeIndex = treeData.findIndex((node) => node.id === nodeId);
    if (nodeIndex !== -1 && !treeData[nodeIndex].children.length) {
      const updatedTreeData = [...treeData];
      const node = updatedTreeData[nodeIndex];
      const updatedNode = await loadNodeChildren(node);
      node.children = updatedNode.children; // Actualizar los hijos del nodo
      setTreeData(updatedTreeData);
    }
  };

  const renderTree = (nodes) => {
    return nodes.map((node) => (
      <TreeItem
        key={node.id}
        nodeId={node.id}
        label={node.name}
        onToggle={handleNodeToggle}
      >
        {Array.isArray(node.children) ? renderTree(node.children) : null}
      </TreeItem>
    ));
  };

  return (
    <ThemeProvider theme={theme}>
      <TreeView
        defaultCollapseIcon={<ExpandMoreIcon />}
        defaultExpandIcon={<ChevronRightIcon />}
      >
        {renderTree(treeData)}
      </TreeView>
    </ThemeProvider>
  );
}

export default TaxonomyTree;
