import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Container, CssBaseline, CircularProgress } from '@mui/material';
import { styled } from '@mui/system';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import { ThemeProvider, createTheme } from '@mui/material/styles';

// Crear un tema personalizado
const theme = createTheme({
  palette: {
    background: {
      default: '#cfd8dc', // Color de fondo de la página
    },
  },
});

const IconTextField = styled(TextField)({
  '& .MuiInputLabel-outlined': {
    fontSize: '1rem',
  },
  '& .MuiOutlinedInput-input': {
    fontSize: '1rem',
    backgroundColor: '#f5f5f5', // Color de fondo de los campos de entrada
  },
  '& .MuiSvgIcon-root': {
    fontSize: '1.5rem',
  },
  '& .Mui-focused .MuiOutlinedInput-notchedOutline': {
    borderColor: '#f5f5f5', // Color del borde cuando el campo está enfocado
  },
});

function ReportForm() {
  const [loading, setLoading] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Lógica para enviar el informe de problema
  };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs" sx={{ bgcolor: '#f5f5f5', py: 5, borderRadius: 2, my: 8, boxShadow: 3 }}>
        <CssBaseline />
        <Box sx={{ display: 'flex', flexDirection: 'column', margin: 'auto', mt: 5, gap: 2 }}>
          <Typography variant="h5" align="center" gutterBottom>
            Formulario de informes de problemas
          </Typography>
          <form onSubmit={handleSubmit}>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
              <IconTextField
                label="Nombre"
                required
                InputProps={{
                  startAdornment: <PersonOutlineIcon color="action" />,
                }}
              />
              <IconTextField
                label="Apellido"
                required
                InputProps={{
                  startAdornment: <PersonOutlineIcon color="action" />,
                }}
              />
              <IconTextField
                label="Correo Electrónico"
                type="email"
                required
                InputProps={{
                  startAdornment: <MailOutlineIcon color="action" />,
                }}
              />
              <IconTextField
                label="Descripción del problema"
                multiline
                rows={4}
                required
                InputProps={{
                  startAdornment: <LockOutlinedIcon color="action" />,
                }}
              />
            </Box>
            <Box sx={{ display: 'flex', justifyContent: 'center' }}>
              <Button type="submit" variant="contained" color="primary" disabled={loading}>
                {loading ? <CircularProgress size={24} /> : 'Enviar informe'}
              </Button>
            </Box>
            {loading && (
              <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
                <CircularProgress />
              </Box>
            )}
          </form>
        </Box>
        <Typography variant="body2" align="justify" sx={{ mt: 2, color: '#777' }}>
          Se ha desarrollado un formulario de informes que permite a los usuarios finales notificar cualquier problema o error de validación que encuentren al interactuar con los datos de especies. Este formulario proporciona campos claros y descriptivos para que los usuarios puedan detallar el problema y proporcionar información adicional relevante.
        </Typography>
      </Container>
    </ThemeProvider>
  );
}

export default ReportForm;
