            </Select>
          </FormControl>
        </div>

        <Button variant="contained" color="primary" onClick={handleSearch}>
          Buscar
        </Button>
      </StyledForm>
      <Typography variant="body1" gutterBottom style={{ color: 'black' }}>
         Opciones seleccionadas: {selectedOptions}
      </Typography>

    </>
  );
