import React, { useEffect, useState } from "react";
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';

export default function TaskList() {
  const [tasks, setTasks] = useState([])
  const [highlightedRow, setHighlightedRow] = useState(null)

  const loadTasks = async () => {
    const response = await fetch('http://localhost:4000/state')
    const data = await response.json()
    setTasks(data)
  }

  useEffect(() => {
    loadTasks()
  }, [])

  return (
    <>
      <h1>Task List</h1>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell style={{ padding: "10px" }}>ID</TableCell>
              <TableCell style={{ padding: "10px" }}>Nombre</TableCell>
              <TableCell style={{ padding: "10px" }} align="right">Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tasks.map((task) => (
              <TableRow 
                key={task.pas_id} 
                sx={{ 
                  '&:last-child td, &:last-child th': { border: 0 },
                  backgroundColor: task.pas_id === highlightedRow ? '#EEE' : '#FFF' 
                }}
                onMouseEnter={() => setHighlightedRow(task.pas_id)}
                onMouseLeave={() => setHighlightedRow(null)}
              >
                <TableCell component="th" scope="row" style={{ padding: "10px" }}>{task.pas_id}</TableCell>
                <TableCell style={{ padding: "10px" }}>{task.pro_nombre}</TableCell>
                <TableCell align="right" style={{ padding: "10px" }}>
                  <Button
                    variant="contained"
                    color="inherit"
                    onClick={() => console.log("edit")}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="contained"
                    color="warning"
                    onClick={() => console.log("delete")}
                    style={{marginLeft: ".5rem"}}
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
