import React, { useState, useEffect } from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem from "@mui/lab/TreeItem";
import axios from "axios";

const StyledTreeView = styled(TreeView)({
  "& .MuiTreeItem-root": {
    border: "1px solid rgba(0, 0, 0, 0.2)",
    borderRadius: "4px",
    margin: "4px",
    padding: "4px",
  },
  "& .MuiTreeItem-content": {
    color: "#000000",
  },
});

export default function FileSystemNavigator() {
  const [reinos, setReinos] = useState([]);

  useEffect(() => {
    fetchReinos();
  }, []);

  const fetchReinos = async () => {
    try {
      const response = await axios.get("http://localhost:4000/reinos");
      setReinos(response.data);
    } catch (error) {
      console.error("Error fetching reinos data: ", error);
    }
  };

  const fetchFilos = async (reinoId) => {
    try {
      const response = await axios.get(`http://localhost:4000/filos/${reinoId}`);
      return response.data;
    } catch (error) {
      console.error("Error fetching filos data: ", error);
      return [];
    }
  };

  const renderFilos = (filos) => {
    return filos.map((filo) => (
      <TreeItem key={filo.fil_id} nodeId={`filo-${filo.fil_id}`} label={filo.fil_nombre.trim()} />
    ));
  };

  const renderReinos = () => {
    return reinos.map((reino) => (
      <TreeItem
        key={reino.rei_id}
        nodeId={`reino-${reino.rei_id}`}
        label={reino.rei_nombre.trim()}
      >
        <TreeItem nodeId={`loading-${reino.rei_id}`} label="Cargando filos..." />

        {reino.filos && reino.filos.length > 0 && (
          <TreeItem nodeId={`filos-${reino.rei_id}`} label="Filos">
            {renderFilos(reino.filos)}
          </TreeItem>
        )}
      </TreeItem>
    ));
  };

  const loadFilos = async (reinoId) => {
    const filos = await fetchFilos(reinoId);
    const updatedReinos = reinos.map((reino) => {
      if (reino.rei_id === reinoId) {
        return { ...reino, filos };
      }
      return reino;
    });
    setReinos(updatedReinos);
  };

  const handleReinoClick = (event, reinoId) => {
    const reino = reinos.find((reino) => reino.rei_id === reinoId);
    if (reino && (!reino.filos || reino.filos.length === 0)) {
      loadFilos(reinoId);
    }
  };

  return (
    <StyledTreeView
      defaultCollapseIcon={<ExpandMoreIcon />}
      defaultExpandIcon={<ChevronRightIcon />}
      onNodeToggle={(event, nodeId) => {
        const reinoId = nodeId.split("-")[1];
        handleReinoClick(event, reinoId);
      }}
    >
      {renderReinos()}
    </StyledTreeView>
  );
}
