import React, { useState, useEffect } from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem, { treeItemClasses } from "@mui/lab/TreeItem";
import { Grid, Typography, Box, TextField, IconButton } from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import axios from 'axios';

const StyledTreeItem = styled(TreeItem)(({ rootNode }) => {
  const borderColor = "gray";

  return {
    position: "relative",
    "&:before": {
      pointerEvents: "none",
      content: '""',
      position: "absolute",
      width: 32,
      left: -16,
      top: 12,
      borderBottom: !rootNode ? `1px dashed ${borderColor}` : "none"
    },
    [`& .${treeItemClasses.group}`]: {
      marginLeft: 16,
      paddingLeft: 18,
      borderLeft: `1px dashed ${borderColor}`
    }
  };
});

const StyledBox = styled(Box)({
  width: "90%",
  fontSize: "1.2em",
  margin: "20px auto",
  textAlign: "center",
  "& p": {
    textAlign: "justify",
    color: "#000000",
    fontFamily: "Helvetica Neue",
    margin: "20px 10px"
  },
  maxWidth: "350px",
  color: "#000000" // Cambia el color del texto a negro
});

const StyledTreeView = styled(TreeView)({
  "& .MuiTreeItem-content": {
    color: "#000000", // Cambiar el color del texto cuando el árbol se despliega
  },
});

export default function TaskForm() {
  const [reinos, setReinos] = useState([]);
  const [filos, setFilos] = useState([]);
  const [selectedReino, setSelectedReino] = useState("");

  const fetchReinos = async () => {
    try {
      const response = await axios.get("http://localhost:4000/reinos");
      setReinos(response.data.map(reino => reino.rei_nombre.trim()));
    } catch (error) {
      console.log("Error fetching reinos data: ", error);
    }
  };

  const fetchFilos = async (reinoId) => {
    try {
      const response = await axios.get(`http://localhost:4000/filos/${reinoId}`);
      setFilos(response.data.map(filo => filo.fil_nombre.trim()));
    } catch (error) {
      console.log("Error fetching filos data: ", error);
    }
  };

  useEffect(() => {
    fetchReinos();
  }, []);

  const handleReinoClick = (reinoId) => {
    setSelectedReino(reinoId);
    fetchFilos(reinoId);
  };

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          {/* Contenedor del Árbol y Trabajo académico */}
          <Box
            border={1}
            borderColor="#333333"
            p={2}
            borderRadius={2}
            minHeight="100vh"
            display="flex"
            flexDirection="column"
          >
            <Grid container spacing={2}>
              <Grid item xs={4}>
                {/* Árbol filogenético */}
                <StyledTreeView
                  defaultCollapseIcon={<ExpandMoreIcon />}
                  defaultExpandIcon={<ChevronRightIcon />}
                  sx={{ height: 240, flexGrow: 1 }}
                >
                  {/* Resto del árbol */}
                  {reinos.map(reino => (
                    <StyledTreeItem
                      key={reino}
                      rootNode
                      nodeId={reino}
                      label={reino}
                      onClick={() => handleReinoClick(reino)}
                    >
                      {filos.length > 0 &&
                        filos.map(filo => (
                          <StyledTreeItem
                            key={filo}
                            nodeId={filo}
                            label={filo}
                          />
                        ))}
                    </StyledTreeItem>
                  ))}
                </StyledTreeView>
              </Grid>
            </Grid>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}
