import React, { useState } from 'react';
import { Button, TextField, Typography } from '@mui/material';
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  button: {
    margin: `${theme.spacing.unit * 2}px`,
  },
  message: {
    marginTop: `${theme.spacing.unit * 2}px`,
    color: 'green',
  },
  errorMessage: {
    marginTop: `${theme.spacing.unit * 2}px`,
    color: 'red',
  },
  formField: {
    marginBottom: `${theme.spacing.unit * 2}px`,
  },
}));

const Formulario = () => {
  const classes = useStyles();
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [filo, setFilo] = useState('');
  const [clase, setClase] = useState('');
  const [orden, setOrden] = useState('');
  const [familia, setFamilia] = useState('');
  const [genero, setGenero] = useState('');
  const [especie, setEspecie] = useState('');

  const handleClick = async () => {
    const filoId = uuidv4();
    const claseId = uuidv4();
    const ordenId = uuidv4();
    const famId = uuidv4();
    const genId = uuidv4();
    const espId = uuidv4();

    try {
      // Ejecutar los queries
      await axios.post('http://localhost:3000/filos', { filo_id: filoId, filo_nombre: filo });
      await axios.post('http://localhost:3000/clases', { cla_id: claseId, fil_id: filoId, cla_nombre: clase });
      await axios.post('http://localhost:3000/ordenes', { ord_id: ordenId, cla_id: claseId, ord_nombre: orden });
      await axios.post('http://localhost:3000/familias', { fam_id: famId, ord_id: ordenId, gen_nombre: familia });
      await axios.post('http://localhost:3000/generos', { gen_id: genId, fam_id: famId, gen_nombre: genero });
      await axios.post('http://localhost:3000/especies', {
        esp_id: espId,
        gen_id: genId,
        esp_nombre: especie,
        esp_sexo: 'macho',
      });

      setSuccessMessage('Los datos se insertaron correctamente');
      setErrorMessage('');
    } catch (error) {
      setSuccessMessage('');
      setErrorMessage('Error al ingresar los datos');
      console.error('Error al ejecutar los queries:', error);
    }
  };

  return (
    <div>
      <Typography variant="body1" className={classes.message}>
        {successMessage}
      </Typography>
      <Typography variant="body1" className={classes.errorMessage}>
        {errorMessage}
      </Typography>
      <TextField
        label="Filo"
        variant="outlined"
        className={classes.formField}
        value={filo}
        onChange={(e) => setFilo(e.target.value)}
      />
      <TextField
        label="Clase"
        variant="outlined"
        className={classes.formField}
        value={clase}
        onChange={(e) => setClase(e.target.value)}
      />
      <TextField
        label="Orden"
        variant="outlined"
        className={classes.formField}
        value={orden}
        onChange={(e) => setOrden(e.target.value)}
      />
      <TextField
        label="Familia"
        variant="outlined"
        className={classes.formField}
        value={familia}
        onChange={(e) => setFamilia(e.target.value)}
      />
      <TextField
        label="GÃ©nero"
        variant="outlined"
        className={classes.formField}
        value={genero}
        onChange={(e) => setGenero(e.target.value)}
      />
      <TextField
        label="Especie"
        variant="outlined"
        className={classes.formField}
        value={especie}
        onChange={(e) => setEspecie(e.target.value)}
      />
      <Button variant="contained" color="primary" className={classes.button} onClick={handleClick}>
        Ejecutar Queries
      </Button>
    </div>
  );
};

export default Formulario;
