import { useEffect, useState } from "react"
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from "@mui/material"

export default function TaskList() {
  const [tasks, setTasks] = useState([])

  const loadTasks = async () => {
    const response = await fetch('http://localhost:4000/state')
    const data = await response.json()
    setTasks(data)
  }

  useEffect(() => {
    loadTasks()
  }, [])

  return (
    <>
      <h1>Task List</h1>
      <TableContainer style={{ marginBottom: "0.2rem" }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Nombre</TableCell>
              <TableCell>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tasks.map((task) => (
              <TableRow key={task.pas_id}>
                <TableCell>{task.pas_id}</TableCell>
                <TableCell>{task.pro_nombre}</TableCell>
                <TableCell>
                  <Button variant="contained" color="inherit" onClick={() => console.log("edit")}>
                    Editar
                  </Button>
                  <Button
                    variant="contained"
                    color="warning"
                    onClick={() => console.log("delete")}
                    style={{ marginLeft: ".5rem" }}
                  >
                    Eliminar
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
