import React, { useEffect, useState } from "react";
import { styled } from "@mui/system";
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';

const StyledTableCell = styled(TableCell)({
  padding: '8px',  // puedes ajustar este valor según tus necesidades
});

const StyledTableRow = styled(TableRow)({
  '&:hover': {
    backgroundColor: 'lightgray',
  },
});

export default function TaskList() {
  const [tasks, setTasks] = useState([])

  const loadTasks = async () => {
    const response = await fetch('http://localhost:4000/consult/Pichincha')
    const data = await response.json()
    setTasks(data)
  }

  useEffect(() => {
    loadTasks()
  }, [])

  return (
    <>
      <h1>Task List</h1>
      <TableContainer component={Paper}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <StyledTableCell>Especie</StyledTableCell>
              <StyledTableCell>Sexo</StyledTableCell>
              <StyledTableCell>Parque Nacional </StyledTableCell>
              <StyledTableCell>Provincia</StyledTableCell>
              <StyledTableCell>Identificador</StyledTableCell>
              <StyledTableCell>Año identificado</StyledTableCell>
              <StyledTableCell>Colector</StyledTableCell>
              <StyledTableCell>Fecha colectado</StyledTableCell>
              <StyledTableCell>Metodo colectar</StyledTableCell>
              <StyledTableCell>Destino</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tasks.map((task) => (
              <StyledTableRow key={task.esp_nombre}>
                <StyledTableCell component="th" scope="row">{task.esp_nombre}</StyledTableCell>
                <StyledTableCell>{task.esp_sexo}</StyledTableCell>
                <StyledTableCell>{task.loc_parque_nacional}</StyledTableCell>
                <StyledTableCell>{task.pro_nombre}</StyledTableCell>
                <StyledTableCell>{task.ide_apellido}</StyledTableCell>
                <StyledTableCell>{task.ide_anio}</StyledTableCell>
                <StyledTableCell>{task.col_apellido}</StyledTableCell>
                <StyledTableCell>{task.col_fecha}</StyledTableCell>
                <StyledTableCell>{task.col_metodo}</StyledTableCell>
                <StyledTableCell>{task.col_destino}</StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
