import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Container, CssBaseline } from '@mui/material';
import { styled } from '@mui/system';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';

const IconTextField = styled(TextField)({
  '& .MuiInputLabel-outlined': {
    fontSize: '1.2rem',
  },
  '& .MuiOutlinedInput-input': {
    fontSize: '1.2rem',
    backgroundColor: '#FFF',
  },
  '& .MuiSvgIcon-root': {
    fontSize: '2rem',
  },
});

function Register() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const handleInputChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      alert('Las contraseñas no coinciden.');
      return;
    }
    // Aquí iría el código para manejar el envío de los datos (ej. llamada API)
  };

  return (
    <Container component="main" maxWidth="xs" sx={{ bgcolor: '#cfe8fc', py: 5, borderRadius: 2, my: 8 }}>
      <CssBaseline />
      <Box sx={{ display: 'flex', flexDirection: 'column', margin: 'auto', mt: 5 }}>
        <Typography variant="h4" align="center" gutterBottom>Registro</Typography>
        <IconTextField
          label="Nombre"
          name="firstName"
          value={formData.firstName}
          onChange={handleInputChange}
          required
          InputProps={{
            startAdornment: <PersonOutlineIcon color="action" />,
          }}
        />
        <IconTextField
          label="Apellido"
          name="lastName"
          value={formData.lastName}
          onChange={handleInputChange}
          required
          InputProps={{
            startAdornment: <PersonOutlineIcon color="action" />,
          }}
        />
        <IconTextField
          label="Correo Electrónico"
          name="email"
          type="email"
          value={formData.email}
          onChange={handleInputChange}
          required
          InputProps={{
            startAdornment: <MailOutlineIcon color="action" />,
          }}
        />
        <IconTextField
          label="Contraseña"
          name="password"
          type="password"
          value={formData.password}
          onChange={handleInputChange}
          required
          InputProps={{
            startAdornment: <LockOutlinedIcon color="action" />,
          }}
        />
        <IconTextField
          label="Confirmar Contraseña"
          name="confirmPassword"
          type="password"
          value={formData.confirmPassword}
          onChange={handleInputChange}
          required
          InputProps={{
            startAdornment: <LockOutlinedIcon color="action" />,
          }}
        />
        <Typography variant="body1" align="justify" sx={{ mt: 2, color: '#777' }}>
          Se recomienda a los usuarios que utilicen contraseñas seguras que combinen letras, números y caracteres especiales para garantizar la seguridad de sus cuentas.
        </Typography>
        <Button type="submit" variant="contained" color="primary" sx={{ mt: 3, fontSize: '1.2rem' }}>
          Registrarse
        </Button>
      </Box>
    </Container>
  );
}

export default Register;
