import React from 'react';
import { TextField, Button, Typography, Box, Container, CssBaseline } from '@mui/material';
import { styled } from '@mui/system';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';

const IconTextField = styled(TextField)({
  '& .MuiInputLabel-outlined': {
    fontSize: '1rem',
  },
  '& .MuiOutlinedInput-input': {
    fontSize: '1rem',
    backgroundColor: '#FFF',
  },
  '& .MuiSvgIcon-root': {
    fontSize: '1.5rem',
  },
});

function Register() {
  return (
    <Container component="main" maxWidth="xs" sx={{ bgcolor: '#f5f5f5', py: 5, borderRadius: 2, my: 8 }}>
      <CssBaseline />
      <Box sx={{ display: 'flex', flexDirection: 'column', margin: 'auto', mt: 5, gap: 2 }}>
        <Typography variant="h5" align="center" gutterBottom>Registro</Typography>
        <IconTextField
          label="Nombre"
          required
          InputProps={{
            startAdornment: <PersonOutlineIcon color="action" />,
          }}
        />
        <IconTextField
          label="Apellido"
          required
          InputProps={{
            startAdornment: <PersonOutlineIcon color="action" />,
          }}
        />
        <IconTextField
          label="Correo Electrónico"
          type="email"
          required
          InputProps={{
            startAdornment: <MailOutlineIcon color="action" />,
          }}
        />
        <IconTextField
          label="Contraseña"
          type="password"
          required
          InputProps={{
            startAdornment: <LockOutlinedIcon color="action" />,
          }}
        />
        <IconTextField
          label="Confirmar Contraseña"
          type="password"
          required
          InputProps={{
            startAdornment: <LockOutlinedIcon color="action" />,
          }}
        />
        <Typography variant="body2" align="justify" sx={{ mt: 2, color: '#777' }}>
          Se recomienda a los usuarios que utilicen contraseñas seguras que combinen letras, números y caracteres especiales para garantizar la seguridad de sus cuentas.
        </Typography>
        <Button type="submit" variant="contained" color="primary" sx={{ mt: 3, fontSize: '1.2rem' }}>
          Registrarse
        </Button>
      </Box>
    </Container>
  );
}

export default Register;
