import { useEffect, useState } from "react"
import { Button, Typography } from "@mui/material"

export default function TaskList() {
  const [tasks, setTasks] = useState([])

  const loadTasks = async () => {
    const response = await fetch('http://localhost:4000/state')
    const data = await response.json()
    setTasks(data)
  }

  useEffect(() => {
    loadTasks()
  }, [])

  return (
    <>
      <h1>Task List</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.pas_id}>
              <td>{task.pas_id}</td>
              <td>{task.pro_nombre}</td>
              <td>
                <Button variant="contained" color="inherit" onClick={() => console.log("edit")}>
                  Editar
                </Button>
                <Button
                  variant="contained"
                  color="warning"
                  onClick={() => console.log("delete")}
                  style={{ marginLeft: ".5rem" }}
                >
                  Eliminar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
