import React, { useEffect, useState } from "react";
import { styled } from "@mui/system";
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';

const StyledTableRow = styled(TableRow)({
  '&:hover': {
    backgroundColor: 'lightgray',
  },
});

export default function TaskList() {
  const [tasks, setTasks] = useState([])

  const loadTasks = async () => {
    const response = await fetch('http://localhost:4000/consult/Pichincha')
    const data = await response.json()
    setTasks(data)
  }

  useEffect(() => {
    loadTasks()
  }, [])

  return (
    <>
      <h1>Task List</h1>
      <TableContainer component={Paper}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Especie</TableCell>
              <TableCell>Sexo</TableCell>
              <TableCell>Parque Nacional </TableCell>
              <TableCell> Provincia </TableCell>
              <TableCell>Identificador </TableCell>
              <TableCell>AÃ±o identificado </TableCell>
              <TableCell>Colector </TableCell>
              <TableCell>Fecha colectado </TableCell>
              <TableCell> Metodo colectar </TableCell>
              <TableCell> Destino</TableCell>

            </TableRow>
          </TableHead>
          <TableBody>
            {tasks.map((task) => (
              <StyledTableRow key={task.esp_nombre}>
                <TableCell component="th" scope="row">{task.esp_nombre}</TableCell>
                <TableCell>{task.esp_sexo}</TableCell>
                <TableCell>{task.loc_parque_nacional}</TableCell>
                <TableCell>{task.pro_nombre}</TableCell>
                <TableCell>{task.ide_apellido}</TableCell>
                <TableCell>{task.ide_anio}</TableCell>
                <TableCell>{task.col_apellido}</TableCell>
                <TableCell>{task.col_fecha}</TableCell>
                <TableCell>{task.col_metodo}</TableCell>
                <TableCell>{task.col_destino}</TableCell>



              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
