import React, { useState } from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem, { treeItemClasses } from "@mui/lab/TreeItem";
import { Grid, Typography, Box, TextField, IconButton } from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";

const StyledTreeItem = styled(TreeItem)(({ rootNode }) => {
  const borderColor = "gray";

  return {
    position: "relative",
    "&:before": {
      pointerEvents: "none",
      content: '""',
      position: "absolute",
      width: 32,
      left: -16,
      top: 12,
      borderBottom: !rootNode ? `1px dashed ${borderColor}` : "none"
    },
    [`& .${treeItemClasses.group}`]: {
      marginLeft: 16,
      paddingLeft: 18,
      borderLeft: `1px dashed ${borderColor}`
    }
  };
});

const StyledBox = styled(Box)({
  width: "90%",
  fontSize: "1.2em",
  margin: "20px auto",
  textAlign: "center",
  "& p": {
    textAlign: "justify",
    color: "#000000",
    fontFamily: "Helvetica Neue",
    margin: "20px 10px"
  },
  maxWidth: "350px",
  color: "#000000" // Cambia el color del texto a negro
});

const StyledTreeView = styled(TreeView)({
  "& .MuiTreeItem-content": {
    color: "#000000", // Cambiar el color del texto cuando el árbol se despliega
  },
});

export default function FileSystemNavigator() {
  const [searchText, setSearchText] = useState("");

  const handleSearchChange = (event) => {
    setSearchText(event.target.value);
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        {/* Descripción */}
        <StyledBox>
          <Typography variant="h5">Descripción</Typography>
          <Typography variant="body1" fontFamily="Helvetica Neue">
            Nuestro sitio web es una plataforma diseñada para facilitar la
            georreferenciación de datos de especies. Con su interfaz fácil de usar
            y su funcionalidad avanzada, permite a los investigadores,
            administradores y usuarios administrar y analizar de manera eficiente
            la información sobre especies.
          </Typography>
        </StyledBox>
      </Grid>
      <Grid item xs={12}>
        {/* Árbol filogenético */}
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <StyledBox>
              <Typography variant="h6">Árbol filogenético</Typography>
              <TextField
                value={searchText}
                onChange={handleSearchChange}
                placeholder="Buscar..."
                variant="outlined"
                size="small"
                fullWidth
                InputProps={{
                  endAdornment: (
                    <IconButton>
                      <SearchIcon />
                    </IconButton>
                  )
                }}
              />
            </StyledBox>
            <StyledTreeView
              defaultCollapseIcon={<ExpandMoreIcon />}
              defaultExpandIcon={<ChevronRightIcon />}
              sx={{ height: 240, flexGrow: 1 }}
            >
              {/* Resto del árbol */}
            </StyledTreeView>
          </Grid>
          <Grid item xs={6}>
            {/* Contenido de la parte derecha */}
            <StyledBox>
              <Typography variant="h6">Trabajo académico</Typography>

            </StyledBox>

            {/* Agrega aquí el contenido que deseas mostrar en la parte derecha */}
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}
