import React, { useState, useEffect } from 'react';
import TreeView from '@mui/lab/TreeView';
import TreeItem from '@mui/lab/TreeItem';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  components: {
    MuiTreeItem: {
      styleOverrides: {
        label: {
          color: '#000000',
        },
        iconContainer: {
          color: '#000000',
        },
      },
    },
  },
});

function TaxonomyTree() {
  const [treeData, setTreeData] = useState([]);

  let nodeIdCounter = 0;

  function createTreeNode(id, name, children, level) {
    nodeIdCounter++;
    const nodeId = id.toString() || `node-${nodeIdCounter}`;
    return { id: nodeId, name: name.trim(), children, level };
  }



  function getNextLevel(currentLevel) {
    switch (currentLevel) {
      case 'reino':
        return 'filo';
      case 'filo':
        return 'clase';
      case 'clase':
        return 'orden';
      case 'orden':
        return 'familia';
      case 'familia':
        return 'genero';
      case 'genero':
        return 'especie';
      default:
        return '';
    }
  }

  async function loadNodeChildren(node) {
    const level = node.level;
    const nextLevel = getNextLevel(level);
    let url = `http://localhost:4000/${nextLevel}s/${node.id}`;

    try {
      const response = await fetch(url);
      const data = await response.json();
      if (data.length > 0) {
        const childNodes = data.map((item) =>
          createTreeNode(item.id, item.nombre, [], nextLevel)
        );
        return { ...node, children: childNodes };
      } else {
        return node;
      }
    } catch (error) {
      console.error('Error fetching data: ', error);
      return node;
    }
  }


  async function fetchData(url, level) {
    try {
      const response = await fetch(url);
      const data = await response.json();
      const nodes = data.map((item) =>
        createTreeNode(item.id, item.nombre, [], level)
      );
      return nodes;
    } catch (error) {
      console.error('Error fetching data: ', error);
      return [];
    }
  }

  useEffect(() => {
    async function initializeTreeData() {
      const reinos = await fetchData('http://localhost:4000/reinos', 'reino');
      setTreeData(reinos);
    }
    initializeTreeData();
  }, []);

  const handleNodeToggle = async (event, nodeIds) => {
    const nodeId = nodeIds[nodeIds.length - 1];
    const nodeIndex = treeData.findIndex((node) => node.id === nodeId);
    if (nodeIndex !== -1 && !treeData[nodeIndex].children.length) {
      const updatedTreeData = [...treeData];
      const node = updatedTreeData[nodeIndex];
      const updatedNode = await loadNodeChildren(node);
      node.children = updatedNode.children; // Actualizar los hijos del nodo
      setTreeData(updatedTreeData);
    }
  };

  const renderTree = (nodes) => {
    return nodes.map((node) => (
      <TreeItem
        key={node.id}
        nodeId={node.id}
        label={node.name}
        onToggle={handleNodeToggle}
      >
        {Array.isArray(node.children) ? renderTree(node.children) : null}
      </TreeItem>
    ));
  };

  return (
    <ThemeProvider theme={theme}>
      <TreeView
        defaultCollapseIcon={<ExpandMoreIcon />}
        defaultExpandIcon={<ChevronRightIcon />}
      >
        {renderTree(treeData)}
      </TreeView>
    </ThemeProvider>
  );
}

export default TaxonomyTree;
