import React, { useState, useEffect } from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem from "@mui/lab/TreeItem";

const StyledTreeItem = styled(TreeItem)(({ rootNode }) => {
  const borderColor = "gray";

  return {
    position: "relative",
    "&:before": {
      pointerEvents: "none",
      content: '""',
      position: "absolute",
      width: 32,
      left: -16,
      top: 12,
      borderBottom: !rootNode ? `1px dashed ${borderColor}` : "none"
    },
    [`& .MuiTreeItem-group`]: {
      marginLeft: 16,
      paddingLeft: 18,
      borderLeft: `1px dashed ${borderColor}`
    }
  };
});

const FileSystemNavigator = () => {
  const [reinos, setReinos] = useState([]);

  useEffect(() => {
    // Realizar la solicitud GET a /reinos para obtener los datos de los reinos
    const fetchReinos = async () => {
      try {
        const response = await fetch("http://localhost:4000/reinos");
        const data = await response.json();
        setReinos(data);
      } catch (error) {
        console.error("Error fetching reinos: ", error);
      }
    };

    fetchReinos();
  }, []);

  const renderReinos = () => {
    return reinos.map((reino) => (
      <StyledTreeItem
        key={reino.rei_id}
        nodeId={reino.rei_id.toString()}
        label={reino.rei_nombre.trim()}
        rootNode
      >
        {reino.filos.length > 0 && renderFilos(reino.filos)}
      </StyledTreeItem>
    ));
  };

  const renderFilos = (filos) => {
    return filos.map((filo) => (
      <StyledTreeItem
        key={filo.fil_id}
        nodeId={filo.fil_id.toString()}
        label={filo.fil_nombre.trim()}
      />
    ));
  };

  return (
    <TreeView
      defaultCollapseIcon={<ExpandMoreIcon />}
      defaultExpandIcon={<ChevronRightIcon />}
    >
      {renderReinos()}
    </TreeView>
  );
};

export default FileSystemNavigator;
