import React, { useState, useEffect } from 'react';
import { Button, TextField, MenuItem } from '@mui/material';
import axios from 'axios';

const TaxonomicForm = () => {
  const [reinos, setReinos] = useState([]);
  const [filos, setFilos] = useState([]);
  const [clases, setClases] = useState([]);
  const [ordenes, setOrdenes] = useState([]);
  const [familias, setFamilias] = useState([]);
  const [generos, setGeneros] = useState([]);
  const [taxonForm, setTaxonForm] = useState({
    reino: '',
    filo: '',
    clase: '',
    orden: '',
    familia: '',
    genero: '',
    especie: '',
  });

  useEffect(() => {
    // Cargar la lista de reinos desde el backend
    axios.get('/api/reinos')
      .then((response) => {
        setReinos(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    if (taxonForm.reino) {
      // Cargar la lista de filos para el reino seleccionado
      axios.get(`/api/filos/${taxonForm.reino}`)
        .then((response) => {
          setFilos(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, [taxonForm.reino]);

  useEffect(() => {
    if (taxonForm.filo) {
      // Cargar la lista de clases para el filo seleccionado
      axios.get(`/api/clases/${taxonForm.filo}`)
        .then((response) => {
          setClases(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, [taxonForm.filo]);

  useEffect(() => {
    if (taxonForm.clase) {
      // Cargar la lista de ordenes para la clase seleccionada
      axios.get(`/api/ordenes/${taxonForm.clase}`)
        .then((response) => {
          setOrdenes(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, [taxonForm.clase]);

  useEffect(() => {
    if (taxonForm.orden) {
      // Cargar la lista de familias para el orden seleccionado
      axios.get(`/api/familias/${taxonForm.orden}`)
        .then((response) => {
          setFamilias(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, [taxonForm.orden]);

  useEffect(() => {
    if (taxonForm.familia) {
      // Cargar la lista de generos para la familia seleccionada
      axios.get(`/api/generos/${taxonForm.familia}`)
        .then((response) => {
          setGeneros(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, [taxonForm.familia]);

  const handleTaxonChange = (event) => {
    setTaxonForm((prevForm) => ({
      ...prevForm,
      [event.target.name]: event.target.value,
    }));
  };

  const handleTaxonSubmit = () => {
    // Enviar los datos del formulario al backend para guardar la especie en la base de datos
    axios.post('/api/especies', taxonForm)
      .then((response) => {
        console.log('Especie guardada:', response.data);
        // Reiniciar el formulario después de guardar la especie
        setTaxonForm({
          reino: '',
          filo: '',
          clase: '',
          orden: '',
          familia: '',
          genero: '',
          especie: '',
        });
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <TextField
        select
        label="Reino"
        name="reino"
        value={taxonForm.reino}
        onChange={handleTaxonChange}
        fullWidth
      >
        {reinos.map((reino) => (
          <MenuItem key={reino.rei_id} value={reino.rei_nombre}>
            {reino.rei_nombre}
          </MenuItem>
        ))}
      </TextField>

      <TextField
        select
        label="Filo"
        name="filo"
        value={taxonForm.filo}
        onChange={handleTaxonChange}
        fullWidth
      >
        {filos.map((filo) => (
          <MenuItem key={filo.fil_id} value={filo.fil_nombre}>
            {filo.fil_nombre}
          </MenuItem>
        ))}
        {taxonForm.reino && (
          <MenuItem value="ingresarNuevo">Ingresar nuevo taxón</MenuItem>
        )}
      </TextField>

      <TextField
        select
        label="Clase"
        name="clase"
        value={taxonForm.clase}
        onChange={handleTaxonChange}
        fullWidth
      >
        {clases.map((clase) => (
          <MenuItem key={clase.cla_id} value={clase.cla_nombre}>
            {clase.cla_nombre}
          </MenuItem>
        ))}
        {taxonForm.filo && !clases.length && (
          <MenuItem value="ingresarNuevo">Ingresar nuevo taxón</MenuItem>
        )}
      </TextField>

      <TextField
        select
        label="Orden"
        name="orden"
        value={taxonForm.orden}
        onChange={handleTaxonChange}
        fullWidth
      >
        {ordenes.map((orden) => (
          <MenuItem key={orden.ord_id} value={orden.ord_nombre}>
            {orden.ord_nombre}
          </MenuItem>
        ))}
        {taxonForm.clase && !ordenes.length && (
          <MenuItem value="ingresarNuevo">Ingresar nuevo taxón</MenuItem>
        )}
      </TextField>

      <TextField
        select
        label="Familia"
        name="familia"
        value={taxonForm.familia}
        onChange={handleTaxonChange}
        fullWidth
      >
        {familias.map((familia) => (
          <MenuItem key={familia.fam_id} value={familia.fam_nombre}>
            {familia.fam_nombre}
          </MenuItem>
        ))}
        {taxonForm.orden && !familias.length && (
          <MenuItem value="ingresarNuevo">Ingresar nuevo taxón</MenuItem>
        )}
      </TextField>

      <TextField
        select
        label="Género"
        name="genero"
        value={taxonForm.genero}
        onChange={handleTaxonChange}
        fullWidth
      >
        {generos.map((genero) => (
          <MenuItem key={genero.gen_id} value={genero.gen_nombre}>
            {genero.gen_nombre}
          </MenuItem>
        ))}
        {taxonForm.familia && !generos.length && (
          <MenuItem value="ingresarNuevo">Ingresar nuevo taxón</MenuItem>
        )}
      </TextField>

      <TextField
        label="Especie"
        name="especie"
        value={taxonForm.especie}
        onChange={handleTaxonChange}
        fullWidth
      />

      <Button variant="contained" color="primary" onClick={handleTaxonSubmit}>
        Guardar
      </Button>
    </div>
  );
};

export default TaxonomicForm;
