import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import { styled } from '@mui/system';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';

const IconTextField = styled(TextField)({
  '& .MuiInputLabel-outlined': {
    fontSize: '1.2rem',
  },
  '& .MuiOutlinedInput-input': {
    fontSize: '1.2rem',
  },
  '& .MuiSvgIcon-root': {
    fontSize: '2rem',
  },
});

function Register() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const handleInputChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      alert('Las contraseñas no coinciden.');
      return;
    }
    // Aquí iría el código para manejar el envío de los datos (ej. llamada API)
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', maxWidth: 400, margin: 'auto', marginTop: 5 }}>
      <Typography variant="h4" align="center" gutterBottom>Registro</Typography>
      <IconTextField
        label="Nombre"
        name="firstName"
        value={formData.firstName}
        onChange={handleInputChange}
        required
        InputProps={{
          startAdornment: <PersonOutlineIcon color="action" />,
        }}
      />
      <IconTextField
        label="Apellido"
        name="lastName"
        value={formData.lastName}
        onChange={handleInputChange}
        required
        InputProps={{
          startAdornment: <PersonOutlineIcon color="action" />,
        }}
      />
      <IconTextField
        label="Correo Electrónico"
        name="email"
        type="email"
        value={formData.email}
        onChange={handleInputChange}
        required
        InputProps={{
          startAdornment: <MailOutlineIcon color="action" />,
        }}
      />
      <IconTextField
        label="Contraseña"
        name="password"
        type="password"
        value={formData.password}
        onChange={handleInputChange}
        required
        InputProps={{
          startAdornment: <LockOutlinedIcon color="action" />,
        }}
      />
      <IconTextField
        label="Confirmar Contraseña"
        name="confirmPassword"
        type="password"
        value={formData.confirmPassword}
        onChange={handleInputChange}
        required
        InputProps={{
          startAdornment: <LockOutlinedIcon color="action" />,
        }}
      />
      <Button type="submit" variant="contained" color="primary" sx={{ mt: 3, fontSize: '1.2rem' }}>
        Registrarse
      </Button>
    </Box>
  );
}

export default Register;
