import React, { useState, useEffect } from 'react';
import TreeView from '@mui/lab/TreeView';
import TreeItem from '@mui/lab/TreeItem';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
    components: {
        MuiTreeItem: {
            styleOverrides: {
                label: {
                    color: '#000000',  // Aquí puedes poner el color que desees para el texto
                },
                iconContainer: {
                    color: '#000000',  // Aquí puedes poner el color que desees para los iconos
                },
            },
        },
    },
});

function TaxonomyTree() {
    const [treeData, setTreeData] = useState([]);

    // Crear un objeto de nodo
    function createTreeNode(id, name, children, level) {
        return { id: id.toString(), name: name.trim(), children, level };
    }

    // Cargar hijos de un nodo
    async function loadNodeChildren(node, level) {
        let url = '';
        let responseData = [];

        switch (level) {
            case 'reino':
                url = `http://localhost:4000/filos/${node.id}`;
                break;
            case 'filo':
                url = `http://localhost:4000/clases/${node.id}`;
                break;
            case 'clase':
                url = `http://localhost:4000/ordenes/${node.id}`;
                break;
            case 'orden':
                url = `http://localhost:4000/familias/${node.id}`;
                break;
            case 'familia':
                url = `http://localhost:4000/generos/${node.id}`;
                break;
            case 'genero':
                url = `http://localhost:4000/especies/${node.id}`;
                break;
            default:
                break;
        }

        try {
            const response = await fetch(url);
            responseData = await response.json();
            console.log(responseData);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }

        const childNodes = responseData.map((item) => {
            const newNode = createTreeNode(item.id, item.nombre, [], level);
            node.children.push(newNode);
            return newNode;
        });

        return childNodes;
    }

    // Manejar evento de expansión de nodo
    const handleNodeToggle = async (nodeId, event) => {
        const nodeIndex = treeData.findIndex(node => node.id === nodeId);
        if (nodeIndex !== -1 && !treeData[nodeIndex].children.length) {
            const nodeLevel = treeData[nodeIndex].level;
            await loadNodeChildren(treeData[nodeIndex], nodeLevel);
            setTreeData([...treeData]);
        }
    };

    // Obtener datos iniciales
    async function fetchData() {
        let responseData = [];

        try {
            const response = await fetch('http://localhost:4000/reinos');
            responseData = await response.json();
            console.log(responseData);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }

        const nodes = responseData.map((item) => createTreeNode(item.rei_id, item.rei_nombre, [], 'reino'));
        setTreeData(nodes);
    }

    useEffect(() => {
        fetchData();
    }, []);

    // Renderizar los nodos del árbol de forma recursiva
    const renderTree = (nodes) => {
        return nodes.map((node) => (
            <TreeItem key={node.id} nodeId={node.id} label={node.name} onToggle={(event) => handleNodeToggle(node.id, event)}>
                {Array.isArray(node.children) ? renderTree(node.children) : null}
            </TreeItem>
        ));
    };

    return (
        <ThemeProvider theme={theme}>
            <TreeView
                defaultCollapseIcon={<ExpandMoreIcon />}
                defaultExpandIcon={<ChevronRightIcon />}
            >
                {renderTree(treeData)}
            </TreeView>
        </ThemeProvider>
    );
}

export default TaxonomyTree;
