import React, { useState } from 'react';
import { TextField, Button } from '@mui/material';
import { makeStyles } from '@mui/styles';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  formContainer: {
    width: '400px',
    margin: 'auto',
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  },
  submitButton: {
    width: '200px',
    margin: 'auto',
  },
}));

const Formulario = () => {
  const classes = useStyles();

  const [reino, setReino] = useState('');
  const [filo, setFilo] = useState('');
  const [clase, setClase] = useState('');
  const [orden, setOrden] = useState('');
  const [familia, setFamilia] = useState('');
  const [genero, setGenero] = useState('');
  const [especie, setEspecie] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:4000/clases', { cla_id: '100', fil_id: '21', cla_nombre: clase });
      await axios.post('http://localhost:4000/ordenes', { ord_id: '200', cla_id: '100', ord_nombre: orden });
      await axios.post('http://localhost:4000/familias', { fam_id: '300', ord_id: '200', gen_nombre: familia });
      await axios.post('http://localhost:4000/generos', { gen_id: '400', fam_id: '300', gen_nombre: genero });
      await axios.post('http://localhost:4000/especies', {
        esp_id: '600',
        gen_id: '400',
        esp_nombre: especie,
        esp_sexo: 'macho',
      });

      console.log('Inserciones exitosas');
    } catch (error) {
      console.error(error);
    }
  };

  const handleExecuteEndpoints = async () => {
    try {
      await axios.post('http://localhost:4000/clases', { cla_id: '100', fil_id: '21', cla_nombre: clase });
      await axios.post('http://localhost:4000/ordenes', { ord_id: '200', cla_id: '100', ord_nombre: orden });
      await axios.post('http://localhost:4000/familias', { fam_id: '300', ord_id: '200', gen_nombre: familia });
      await axios.post('http://localhost:4000/generos', { gen_id: '400', fam_id: '300', gen_nombre: genero });
      await axios.post('http://localhost:4000/especies', {
        esp_id: '600',
        gen_id: '400',
        esp_nombre: especie,
        esp_sexo: 'macho',
      });

      console.log('Endpoints ejecutados');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className={classes.formContainer}>
      <TextField label="Reino" value={reino} onChange={(e) => setReino(e.target.value)} />
      <TextField label="Filo" value={filo} onChange={(e) => setFilo(e.target.value)} />
      <TextField label="Clase" value={clase} onChange={(e) => setClase(e.target.value)} />
      <TextField label="Orden" value={orden} onChange={(e) => setOrden(e.target.value)} />
      <TextField label="Familia" value={familia} onChange={(e) => setFamilia(e.target.value)} />
      <TextField label="GÃ©nero" value={genero} onChange={(e) => setGenero(e.target.value)} />
      <TextField label="Especie" value={especie} onChange={(e) => setEspecie(e.target.value)} />

      <Button type="submit" variant="contained" color="primary" className={classes.submitButton}>
        Ingresar
      </Button>

      <Button
        variant="contained"
        color="secondary"
        className={classes.submitButton}
        onClick={handleExecuteEndpoints}
      >
        Ejecutar Endpoints
      </Button>
    </form>
  );
};

export default Formulario;
