import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from "@mui/material"
import { useState, useEffect } from "react";
import { useNavigate} from 'react-router-dom'


export default function TaxonomicTree() {
  const [taxonomicTree, setTaxonomicTree] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/taxonTree')
      .then(response => response.json())
      .then(data => setTaxonomicTree(data))
      .catch(error => console.error('Error:', error));
  }, []);

  const renderTree = (nodes) => (
    <StyledTreeItem key={nodes.id} nodeId={nodes.id} label={nodes.name}>
      {Array.isArray(nodes.children) ? nodes.children.map((node) => renderTree(node)) : null}
    </StyledTreeItem>
  );

  return (
    <TreeView
      defaultCollapseIcon={<ExpandMoreIcon />}
      defaultExpandIcon={<ChevronRightIcon />}
    >
      {taxonomicTree.map((tree) => renderTree(tree))}
    </TreeView>
  );
}

const StyledTreeItem = styled((props) => <TreeItem {...props} />)(({ theme }) => ({
  [`& .${treeItemClasses.label}`]: {
    fontWeight: 'inherit',
    color: 'inherit',
  },
  [`& .${treeItemClasses.group}`]: {
    marginLeft: 7,
    paddingLeft: 18,
    borderLeft: `1px dashed ${fade(theme.palette.text.primary, 0.4)}`,
  },
}));
