import React, { useState } from 'react';
import { Button, TextField } from '@mui/material';
import axios from 'axios';

const TaxonomicForm = () => {
  const [taxonForm, setTaxonForm] = useState({
    reino: '',
    filo: '',
    clase: '',
    orden: '',
    familia: '',
    genero: '',
    especie: '',
  });

  const handleTaxonChange = (event) => {
    setTaxonForm((prevForm) => ({
      ...prevForm,
      [event.target.name]: event.target.value,
    }));
  };

  const handleTaxonSubmit = () => {
    axios.post('/api/especies', taxonForm)
      .then((response) => {
        console.log('Especie guardada:', response.data);
        setTaxonForm({
          reino: '',
          filo: '',
          clase: '',
          orden: '',
          familia: '',
          genero: '',
          especie: '',
        });
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <TextField
        label="Reino"
        name="reino"
        value={taxonForm.reino}
        onChange={handleTaxonChange}
        fullWidth
      />

      <TextField
        label="Filo"
        name="filo"
        value={taxonForm.filo}
        onChange={handleTaxonChange}
        fullWidth
      />

      <TextField
        label="Clase"
        name="clase"
        value={taxonForm.clase}
        onChange={handleTaxonChange}
        fullWidth
      />

      <TextField
        label="Orden"
        name="orden"
        value={taxonForm.orden}
        onChange={handleTaxonChange}
        fullWidth
      />

      <TextField
        label="Familia"
        name="familia"
        value={taxonForm.familia}
        onChange={handleTaxonChange}
        fullWidth
      />

      <TextField
        label="Género"
        name="genero"
        value={taxonForm.genero}
        onChange={handleTaxonChange}
        fullWidth
      />

      <TextField
        label="Especie"
        name="especie"
        value={taxonForm.especie}
        onChange={handleTaxonChange}
        fullWidth
      />

      <Button variant="contained" color="primary" onClick={handleTaxonSubmit}>
        Guardar
      </Button>
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <h1>Ingreso de Datos Taxonómicos</h1>
      <TaxonomicForm />
    </div>
  );
};

export default App;
