import React, { useState, useEffect } from 'react';
import TreeView from '@mui/lab/TreeView';
import TreeItem from '@mui/lab/TreeItem';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
    components: {
        MuiTreeItem: {
            styleOverrides: {
                label: {
                    color: '#000000',
                },
                iconContainer: {
                    color: '#000000',
                },
            },
        },
    },
});

function TaxonomyTree() {
    const [treeData, setTreeData] = useState([]);

    function createTreeNode(id, name, children, level) {
        return { id: id.toString(), name: name.trim(), children, level };
    }

    async function loadNodeChildren(node, level) {
        let url = '';

        switch (level) {
            case 'reino':
                url = `http://localhost:4000/filos/${node.id}`;
                break;
            case 'filo':
                url = `http://localhost:4000/clases/${node.id}`;
                break;
            case 'clase':
                url = `http://localhost:4000/ordenes/${node.id}`;
                break;
            case 'orden':
                url = `http://localhost:4000/familias/${node.id}`;
                break;
            case 'familia':
                url = `http://localhost:4000/generos/${node.id}`;
                break;
            case 'genero':
                url = `http://localhost:4000/especies/${node.id}`;
                break;
            default:
                break;
        }

        try {
            const response = await fetch(url);
            const data = await response.json();

            return data.map(item => createTreeNode(item.id, item.nombre, [], level));
        } catch (error) {
            console.error('Error fetching data: ', error);
        }

        return [];
    }

    const handleNodeToggle = async (event, nodeIds) => {
        if (nodeIds.length === 0) return;

        const expandingNodeId = nodeIds[nodeIds.length - 1];
        const nodeIndex = treeData.findIndex(node => node.id === expandingNodeId);
        if (nodeIndex === -1) return;

        const node = treeData[nodeIndex];
        if (node.children.length !== 0) return;

        const newChildren = await loadNodeChildren(node, node.level);
        const newNode = { ...node, children: newChildren };
        const newTreeData = [...treeData];
        newTreeData[nodeIndex] = newNode;

        setTreeData(newTreeData);
    };

    async function fetchData() {
        try {
            const response = await fetch('http://localhost:4000/reinos');
            const data = await response.json();

            const nodes = data.map(item => createTreeNode(item.rei_id, item.rei_nombre, [], 'reino'));
            setTreeData(nodes);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }
    }

    useEffect(() => {
        fetchData();
    }, []);

    const renderTree = (nodes) => {
        return nodes.map((node) => (
            <TreeItem key={node.id} nodeId={node.id} label={node.name}>
                {Array.isArray(node.children) ? renderTree(node.children) : null}
            </TreeItem>
        ));
    };

    return (
        <ThemeProvider theme={theme}>
            <TreeView
                defaultCollapseIcon={<ExpandMoreIcon />}
                defaultExpandIcon={<ChevronRightIcon />}
                onNodeToggle={handleNodeToggle}
            >
                {renderTree(treeData)}
            </TreeView>
        </ThemeProvider>
    );
}

export default TaxonomyTree;
