import React, { useState, useEffect } from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem, { treeItemClasses } from "@mui/lab/TreeItem";
import { Grid, Typography, Box } from "@mui/material";
import axios from "axios";

const StyledTreeItem = styled(TreeItem)(({ rootNode }) => {
  const borderColor = "gray";

  return {
    position: "relative",
    "&:before": {
      pointerEvents: "none",
      content: '""',
      position: "absolute",
      width: 32,
      left: -16,
      top: 12,
      borderBottom: !rootNode ? `1px dashed ${borderColor}` : "none"
    },
    [`& .${treeItemClasses.group}`]: {
      marginLeft: 16,
      paddingLeft: 18,
      borderLeft: `1px dashed ${borderColor}`
    }
  };
});

const StyledBox = styled(Box)({
  width: "90%",
  fontSize: "1.2em",
  margin: "20px auto",
  textAlign: "center",
  "& p": {
    textAlign: "justify",
    color: "#000000",
    fontFamily: "Helvetica Neue",
    margin: "20px 10px"
  },
  maxWidth: "250px",
  color: "#000000" // Cambia el color del texto a negro
});

const StyledTreeView = styled(TreeView)({
  "& .MuiTreeItem-content": {
    color: "#000000", // Cambiar el color del texto cuando el Ã¡rbol se despliega
  },
});


export default function FileSystemNavigator() {
  const [reinos, setReinos] = useState([]);
  const [filos, setFilos] = useState([]);
  const [clases, setClases] = useState([]);
  const [ordenes, setOrdenes] = useState([]);
  const [searchText, setSearchText] = useState("");

  useEffect(() => {
    // Obtener los reinos al cargar el componente
    fetchReinos();
  }, []);

  const fetchReinos = async () => {
    try {
      const response = await axios.get("http://localhost:4000/reinos");
      setReinos(response.data);
    } catch (error) {
      console.error("Error fetching reinos data: ", error);
    }
  };

  const fetchFilos = async (reinoId) => {
    try {
      const response = await axios.get(`http://localhost:4000/filos/${reinoId}`);
      setFilos(response.data);
      setClases([]); // Reiniciar las clases cuando se cambia de reino
      setOrdenes([]); // Reiniciar los ordenes cuando se cambia de reino
    } catch (error) {
      console.error("Error fetching filos data: ", error);
    }
  };

  const fetchClases = async (filoId) => {
    try {
      const response = await axios.get(`http://localhost:4000/clases/${filoId}`);
      setClases(response.data);
      setOrdenes([]); // Reiniciar los ordenes cuando se cambia de filo
    } catch (error) {
      console.error("Error fetching clases data: ", error);
    }
  };

  const fetchOrdenes = async (claseId) => {
    try {
      const response = await axios.get(`http://localhost:4000/ordenes/${claseId}`);
      setOrdenes(response.data);
    } catch (error) {
      console.error("Error fetching ordenes data: ", error);
    }
  };

  const handleReinoClick = (event, nodeId) => {
    if (nodeId) {
      const reinoId = nodeId.split("-")[1];
      const reino = reinos.find((reino) => reino.rei_id.toString() === reinoId);
      if (reino) {
        fetchFilos(reinoId);
      }
    }
  };

  const handleFiloClick = (event, nodeId) => {
    if (nodeId) {
      const filoId = nodeId.split("-")[1];
      const filo = filos.find((filo) => filo.fil_id.toString() === filoId);
      if (filo) {
        fetchClases(filoId);
      }
    }
  };

  const handleClaseClick = (event, nodeId) => {
    if (nodeId) {
      const claseId = nodeId.split("-")[1];
      const clase = clases.find((clase) => clase.cla_id.toString() === claseId);
      if (clase) {
        fetchOrdenes(claseId);
      }
    }
  };

  const renderReinos = () => {
    return reinos.map((reino) => (
      <StyledTreeItem
        key={reino.rei_id}
        nodeId={`reino-${reino.rei_id}`}
        label={reino.rei_nombre.trim()}
        onClick={(event) => handleReinoClick(event, `reino-${reino.rei_id}`)}
      />
    ));
  };

  const renderFilos = () => {
    return filos.map((filo) => (
      <StyledTreeItem
        key={filo.fil_id}
        nodeId={`filo-${filo.fil_id}`}
        label={filo.fil_nombre.trim()}
        onClick={(event) => handleFiloClick(event, `filo-${filo.fil_id}`)}
      />
    ));
  };

  const renderClases = () => {
    return clases.map((clase) => (
      <StyledTreeItem
        key={clase.cla_id}
        nodeId={`clase-${clase.cla_id}`}
        label={clase.cla_nombre.trim()}
        onClick={(event) => handleClaseClick(event, `clase-${clase.cla_id}`)}
      />
    ));
  };

  const renderOrdenes = () => {
    return ordenes.map((orden) => (
      <StyledTreeItem
        key={orden.ord_id}
        nodeId={`orden-${orden.ord_id}`}
        label={orden.ord_nombre.trim()}
      />
    ));
  };

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          {/* Contenedor del Ãrbol y Trabajo acadÃ©mico */}
          <Box
            border={1}
            borderColor="#333333"
            p={2}
            borderRadius={2}
            minHeight="100vh"
            display="flex"
            flexDirection="column"
          >
            <Grid container spacing={2}>
              <Grid item xs={3}>
                {/* Ãrbol filogenÃ©tico */}
                <TreeView
                  defaultCollapseIcon={<ExpandMoreIcon />}
                  defaultExpandIcon={<ChevronRightIcon />}
                  sx={{ height: 240, flexGrow: 1 }}
                >
                  {/* Resto del Ã¡rbol */}
                  {renderReinos()}
                </TreeView>
              </Grid>
              <Grid item xs={3}>
                {/* Filos */}
                {filos.length > 0 && (
                  <TreeView
                    defaultCollapseIcon={<ExpandMoreIcon />}
                    defaultExpandIcon={<ChevronRightIcon />}
                  >
                    {renderFilos()}
                  </TreeView>
                )}
              </Grid>
              <Grid item xs={3}>
                {/* Clases */}
                {clases.length > 0 && (
                  <TreeView
                    defaultCollapseIcon={<ExpandMoreIcon />}
                    defaultExpandIcon={<ChevronRightIcon />}
                  >
                    {renderClases()}
                  </TreeView>
                )}
              </Grid>
              <Grid item xs={3}>
                {/* Ordenes */}
                {ordenes.length > 0 && (
                  <TreeView
                    defaultCollapseIcon={<ExpandMoreIcon />}
                    defaultExpandIcon={<ChevronRightIcon />}
                  >
                    {renderOrdenes()}
                  </TreeView>
                )}
              </Grid>
            </Grid>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}
