import React, { useState } from 'react';
import { Button, ButtonGroup, Typography, Paper, Container, CssBaseline, Box, Link, Avatar, Grid } from '@mui/material';
import { styled } from '@mui/system';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import TableCell from '@mui/material/TableCell';
import TableRow from '@mui/material/TableRow';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import SupervisorAccountIcon from '@mui/icons-material/SupervisorAccount';
import ScienceIcon from '@mui/icons-material/Science';
import BuildIcon from '@mui/icons-material/Build';
import SaveIcon from '@mui/icons-material/Save';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  color: theme.palette.common.black,
  border: '1px solid #ddd',
  padding: '0.5rem',
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: '#ffffff',
  },
  '&:nth-of-type(even)': {
    backgroundColor: '#f5f5f5',

  },
}));

const StyledButton = styled(Button)(({ theme }) => ({
  margin: '0 5px',
}));

const users = [
  { id: 1, name: 'Juan Araque', email: 'jaraque129@puce.edu.ec', role: 'Administrador', avatar: 'https://example.com/profile1.png' },
  { id: 2, name: 'Usuario Puce', email: 'usuario@puce.edu.ec', role: 'Investigador', avatar: 'https://example.com/profile2.png' },
  { id: 3, name: 'Usuario Puce', email: 'usuario@puce.edu.ec', role: 'Técnico', avatar: 'https://example.com/profile3.png' },
  { id: 4, name: 'Usuario Puce', email: 'usuario@puce.edu.ec', role: 'Investigador', avatar: 'https://example.com/profile4.png' },
  { id: 5, name: 'Usuario Puce', email: 'usuario@puce.edu.ec', role: 'Técnico', avatar: 'https://example.com/profile5.png' },
];

const theme = createTheme({
  typography: {
    fontFamily: 'Helvetic', // Cambia "Roboto, sans-serif" por la fuente que desees
  },
});

function RoleAssignment() {
  const [buttonColors, setButtonColors] = useState(Array(users.length * 3).fill("primary"));

  const handleClick = (index) => {
    const newButtonColors = [...buttonColors];
    newButtonColors[index] = newButtonColors[index] === "primary" ? "secondary" : "primary";
    setButtonColors(newButtonColors);
  };

  return (
    <ThemeProvider theme={theme}>
      <div>
        <AppBar position="static" color="transparent" elevation={0}>
          <Container maxWidth="lg">
            <Toolbar>
              <Typography variant="h6" component={Link} to="/" style={{ fontSize: '1.6rem', fontWeight: 'bold', textDecoration: 'none', color: '#333' }}>
                <span style={{ fontFamily: 'Helvetica, Arial, sans-serif' }}>Bio-Inka</span>
              </Typography>
              <Box style={{ display: 'flex', gap: '1.5rem' }}>
                <Link to="/inicio/geo" style={{ fontSize: '1.2rem', color: '#333', textDecoration: 'none', padding: '0.5rem', borderRadius: '0.25rem', transition: 'background-color 0.3s ease', fontFamily: 'Roboto, sans-serif' }}>Georreferenciación</Link>
                <Link to="/inicio/iniciova" style={{ fontSize: '1.2rem', color: '#333', textDecoration: 'none', padding: '0.5rem', borderRadius: '0.25rem', transition: 'background-color 0.3s ease', fontFamily: 'Roboto, sans-serif' }}>Destacados</Link>
                <Link to="/inicio/about" style={{ fontSize: '1.2rem', color: '#333', textDecoration: 'none', padding: '0.5rem', borderRadius: '0.25rem', transition: 'background-color 0.3s ease', fontFamily: 'Roboto, sans-serif' }}>Acerca de nosotros</Link>
                <Link to="/inicio/login" style={{ fontSize: '1.2rem', color: '#333', textDecoration: 'none', padding: '0.5rem', borderRadius: '0.25rem', transition: 'background-color 0.3s ease', fontFamily: 'Roboto, sans-serif' }}>Log-In</Link>
              </Box>
            </Toolbar>
          </Container>
        </AppBar>

        <Container component="main" maxWidth="md" sx={{ py: 5, my: 8, boxShadow: 3 }}>
          <CssBaseline />

          <Grid container spacing={2}>
            <Grid item xs={12} md={4}>
              <Box display="flex" flexDirection="column" alignItems="center">
                <Avatar src={users[0].avatar} sx={{ width: 80, height: 80, marginBottom: 2 }} />
                <Typography variant="subtitle1">{users[0].role}</Typography>
                <Typography variant="subtitle2">{users[0].name}</Typography>
                <Typography variant="caption">{users[0].email}</Typography>
              </Box>
            </Grid>

            <Grid item xs={12} md={8}>
              <Typography variant="h5" align="center" gutterBottom>
                Asignación de Roles
              </Typography>

              <Typography variant="body1" gutterBottom>
                Se ha implementado la funcionalidad de asignación de roles en el sistema, brindando a los administradores la capacidad de asignar roles específicos a los usuarios. Los roles disponibles son:
              </Typography>

              <ul>
                <li>
                  <Typography variant="body1" component="span" fontWeight="bold">
                    Administrador:
                  </Typography>{' '}
                  este rol tiene acceso completo a todas las características y funcionalidades del sistema. Como administrador, serías responsable de administrar las cuentas de los usuarios, asignar funciones y permisos, y realizar tareas administrativas, como copias de seguridad de datos y mantenimiento del sistema.
                </li>
                <li>
                  <Typography variant="body1" component="span" fontWeight="bold">
                    Investigador:
                  </Typography>{' '}
                  este rol tendría acceso a las funciones de administración de datos del sistema, incluida la capacidad de agregar, modificar y eliminar datos de insectos.
                </li>
                <li>
                  <Typography variant="body1" component="span" fontWeight="bold">
                    Técnico:
                  </Typography>{' '}
                  este rol sería responsable de administrar los componentes de hardware y software del sistema. Como técnico, usarías las funciones de monitoreo y resolución de problemas del sistema para garantizar que el sistema funcione sin problemas y para abordar cualquier problema que surja.
                </li>
              </ul>

              <TableContainer component={Paper} sx={{ backgroundColor: 'transparent' }}>
                <Table sx={{ minWidth: 700 }} aria-label="customized table">
                  <TableHead>
                    <TableRow>
                      <StyledTableCell style={{ backgroundColor: '#f5f5f5', fontWeight: 'bold', fontFamily: 'Helvetica Neue' }}>Usuarios</StyledTableCell>
                      <StyledTableCell style={{ backgroundColor: '#f5f5f5', fontWeight: 'bold', fontFamily: 'Helvetica Neue' }}>Email</StyledTableCell>
                      <StyledTableCell style={{ backgroundColor: '#f5f5f5', fontWeight: 'bold', fontFamily: 'Helvetica Neue' }}>Asignar Rol</StyledTableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {users.map((user, index) => (
                      <StyledTableRow key={user.id}>
                        <StyledTableCell component="th" scope="row" style={{ fontFamily: 'Helvetica Neue' }}>
                          {user.name}
                        </StyledTableCell>
                        <StyledTableCell style={{ fontFamily: 'Helvetica Neue' }}>{index === 0 ? '' : user.email}</StyledTableCell>
                        <StyledTableCell style={{ fontFamily: 'Helvetica Neue' }}>
                          <ButtonGroup variant="text" aria-label="outlined primary button group">
                            <StyledButton startIcon={<SupervisorAccountIcon />} variant={buttonColors[index * 3] === 'primary' ? 'outlined' : 'contained'} color="primary" onClick={() => handleClick(index * 3)}>
                              Administrador
                            </StyledButton>
                            <StyledButton startIcon={<ScienceIcon />} variant={buttonColors[index * 3 + 1] === 'primary' ? 'outlined' : 'contained'} color="primary" onClick={() => handleClick(index * 3 + 1)}>
                              Investigador
                            </StyledButton>
                            <StyledButton startIcon={<BuildIcon />} variant={buttonColors[index * 3 + 2] === 'primary' ? 'outlined' : 'contained'} color="primary" onClick={() => handleClick(index * 3 + 2)}>
                              Técnico
                            </StyledButton>
                          </ButtonGroup>
                        </StyledTableCell>
                      </StyledTableRow>
                    ))}
                  </TableBody>
                </Table>
                <Button variant="contained" color="primary" startIcon={<SaveIcon />} style={{ marginTop: '2px', marginBottom: '1px', float: 'right' }}>
                  Guardar
                </Button>
              </TableContainer>
            </Grid>
          </Grid>
        </Container>
      </div>
    </ThemeProvider>
  );
}

export default RoleAssignment;
