import React, { useEffect, useState } from "react";
import { styled } from "@mui/system";
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Typography } from '@mui/material';
import bannerImage from '../imagenes/bannerInicio.jpg';  // Asegúrate de cambiar esto a la ruta correcta de tu imagen
import { Box, FormControl, InputLabel, Select, MenuItem, Typograph } from '@mui/material';

import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';



const ContentContainer = styled('div')({
  display: 'flex',
  justifyContent: 'space-between',
  width: '100%',
  '@media (max-width:600px)': {
    flexDirection: 'column',
    alignItems: 'center',
  },
});


const StyledTableCell = styled(TableCell)({
  padding: '4px',
});

const StyledTableHeaderCell = styled(TableCell)({
  padding: '8px',
  backgroundColor: '#3f51b5', // Color de fondo del encabezado
  color: '#fff', // Color del texto del encabezado
  fontSize: '1.1em', // Tamaño de la fuente del encabezado
});

const StyledTableRow = styled(TableRow)({
  '&:nth-of-type(odd)': {
    backgroundColor: '#f4f6f8', // Puedes cambiar a cualquier color que te guste.
  },
  '&:hover': {
    backgroundColor: 'lightgray',
  },
});

const StyledTableContainer = styled(TableContainer)({
  borderRadius: '10px',  // Puedes ajustar el valor a tu gusto.
  boxShadow: '0px 0px 10px 0px rgba(0,0,0,0.2)', // Sombra alrededor de la tabla.
  overflow: 'auto', // Permite el desplazamiento cuando el contenido supera la altura máxima.
  maxHeight: '400px', // Ajusta esto a la altura que desees.
});

export default function TaskList() {




  return (
    <>
      <ContentContainer>
        <Box sx={{
          width: { xs: '90%', sm: '35%' },
          fontSize: '1.2em',
          margin: '20px 20px',
          '& p': {
            textAlign: 'justify',
            color: '#000000',
            fontFamily: 'Helvetica Neue',
            margin: '20px 10px'
          },
          textAlign: 'center' // Agregado para centrar el texto
        }}>
          <Typography variant="h5" sx={{ color: '#000000', fontFamily: 'Helvetica Neue', margin: '20px 10px' }}>Descripción</Typography>
          <Typography variant="body1" color="#000000" fontFamily="Helvetica Neue">Nuestro sitio web es una plataforma diseñada para facilitar la georreferenciación de datos de especies. Con su interfaz fácil de usar y su funcionalidad avanzada, permite a los investigadores, administradores y usuarios administrar y analizar de manera eficiente la información sobre especies.</Typography>
          <p>Nuestro sitio web es una plataforma diseñada para facilitar la georreferenciación de datos de especies. Con su interfaz fácil de usar y su funcionalidad avanzada, permite a los investigadores, administradores y usuarios administrar y analizar de manera eficiente la información sobre especies.</p>
        </Box>
        <Box sx={{ width: { xs: '100%', sm: '45%' }, fontSize: '1.2em', margin: '0 10px' }}>



        </Box>
      </ContentContainer>

    </>
  );
}
