import React, { useContext, useState } from 'react';
import { TextField, Button } from '@mui/material';
import { AuthContext } from './AuthContext';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { setCredencialesValidas } = useContext(AuthContext);

  const handleInicioSesion = () => {
    // Verificar las credenciales aquí
    if (email === 'jaraque@puce.edu.ec' && password === 'jnc18241') {
      setCredencialesValidas(true);
      window.open('https://www.google.com', '_blank');
    } else {
      alert('Credenciales inválidas');
    }
  };

  return (
    <div>
      <TextField
        label="Correo Electrónico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <TextField
        label="Contraseña"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <Button onClick={handleInicioSesion}>Iniciar sesión</Button>
    </div>
  );
}

export default Login;
