import React, { useState, useEffect } from "react";
import TreeView from "@mui/lab/TreeView";
import { styled } from "@mui/material/styles";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem from "@mui/lab/TreeItem";

const StyledTreeItem = styled(TreeItem)(({ rootNode }) => {
  const borderColor = "gray";

  return {
    position: "relative",
    "&:before": {
      pointerEvents: "none",
      content: '""',
      position: "absolute",
      width: 32,
      left: -16,
      top: 12,
      borderBottom: !rootNode ? `1px dashed ${borderColor}` : "none"
    },
    [`& .MuiTreeItem-group`]: {
      marginLeft: 16,
      paddingLeft: 18,
      borderLeft: `1px dashed ${borderColor}`
    }
  };
});

const FileSystemNavigator = () => {
  const [treeData, setTreeData] = useState([]);

  useEffect(() => {
    // Realizar la solicitud GET a /reinos para obtener los datos del árbol
    const fetchTreeData = async () => {
      try {
        const response = await fetch("http://localhost:4000/reinos");
        const data = await response.json();
        setTreeData(data);
      } catch (error) {
        console.error("Error fetching tree data: ", error);
      }
    };

    fetchTreeData();
  }, []);

  const renderTree = (nodes) => {
    return nodes.map((node) => (
      <StyledTreeItem
        key={node.rei_id}
        nodeId={node.rei_id.toString()}
        label={node.rei_nombre.trim()}
        rootNode
      >
        {node.filos.length > 0 && renderTreeFilos(node.filos)}
      </StyledTreeItem>
    ));
  };

  const renderTreeFilos = (filos) => {
    return filos.map((filo) => (
      <StyledTreeItem
        key={filo.fil_id}
        nodeId={filo.fil_id.toString()}
        label={filo.fil_nombre.trim()}
      >
        {/* Agregar lógica para renderizar los nodos de clases, órdenes, etc. aquí */}
      </StyledTreeItem>
    ));
  };

  return (
    <TreeView
      defaultCollapseIcon={<ExpandMoreIcon />}
      defaultExpandIcon={<ChevronRightIcon />}
    >
      {renderTree(treeData)}
    </TreeView>
  );
};

export default FileSystemNavigator;
